#include <bits/stdc++.h>

using namespace std;

const int MAXN = 103;
const int INF = 1 << 30;

int n, k;
int p[MAXN];
int c[MAXN];
int l[MAXN];

struct edge {
	int a, b, cap, flow;
};

struct dinic {
  int n, s, t, d[MAXN], ptr[MAXN], q[MAXN];
  vector<edge> e;
  vector<int> g[MAXN];

  reset() {
    e.clear();
    for (int i =0; i < MAXN; i++) g[i].clear();
  }

  void add_edge (int a, int b, int cap) {
    //cout << "add from " << a << " to " << b << " with " << cap << endl;
	  edge e1 = { a, b, cap, 0 };
	  edge e2 = { b, a, 0, 0 };
    g[a].push_back ((int) e.size());
	  e.push_back (e1);
	  g[b].push_back ((int) e.size());
	  e.push_back (e2);
  }

  bool bfs() {
	  int qh=0, qt=0;
	  q[qt++] = s;
	  memset (d, -1, n * sizeof d[0]);
	  d[s] = 0;
	  while (qh < qt && d[t] == -1) {
		  int v = q[qh++];
		  for (size_t i=0; i<g[v].size(); ++i) {
			  int id = g[v][i], to = e[id].b;
			  if (d[to] == -1 && e[id].flow < e[id].cap) {
				  q[qt++] = to;
				  d[to] = d[v] + 1;
			  }
		  }
	  }
	  return d[t] != -1;
  }

  int dfs (int v, int flow) {
	  if (!flow)  return 0;
	  if (v == t)  return flow;
	  for (; ptr[v]<(int)g[v].size(); ++ptr[v]) {
		  int id = g[v][ptr[v]], to = e[id].b;
		  if (d[to] != d[v] + 1)  continue;
		  int pushed = dfs (to, min (flow, e[id].cap - e[id].flow));
		  if (pushed) {
			  e[id].flow += pushed;
			  e[id^1].flow -= pushed;
			  return pushed;
		  }
	  }
	  return 0;
  }

  int f(int st, int en, int co) {
    s = st;
    t = en;
    n = co;
	  int flow = 0;
	  for (;;) {
		  if (!bfs())  break;
		  memset (ptr, 0, n * sizeof ptr[0]);
		  while (int pushed = dfs (s, INF))
			  flow += pushed;
	  }

    // cout << "flow:" << flow << endl;
    return flow;
  }
} d;

inline bool is_prime(int number ) {
 if ( ( (!(number & 1)) && number != 2 ) || (number < 2) || (number % 3 == 0 && number != 3) )
  return false;

 for( int k = 1; 36*k*k-12*k < number;++k)
  if ( (number % (6*k+1) == 0) || (number % (6*k-1) == 0) )
   return false;
 return true;
}

int solve (int level) {
  d.reset();
  vector<int> v, v1;  

  int p1max = 0, c1c = -1;
  int sum = 0;

  for (int i = 0; i < n; i++)
    if (l[i] <= level) {
      if (c[i] == 1 && p[i] > p1max) {
        p1max = p[i]; c1c = i; continue;
      }
      sum += p[i];
      if (c[i]%2)v.push_back(i);
       else v1.push_back(i);
    }

  if (c1c > -1) {v.push_back(c1c); sum += p[c1c];}

  for (int i = 0; i < v.size(); i++) {
    for (int j = 0; j < v1.size(); j++) {
      if (is_prime(c[v[i]] + c[v1[j]])) {
        d.add_edge(v[i], v1[j], INF);
      }
    }
    d.add_edge(MAXN-2, v[i], p[v[i]]);
  }
  for (int j = 0; j < v1.size(); j++) {
    d.add_edge(v1[j], MAXN-1, p[v1[j]]);
  }

  //cout << "sum:" << sum << endl;
  return sum - d.f(MAXN-2, MAXN-1, MAXN);
}

