import java.util.*;
import java.io.*;

public class A {
    static BufferedReader rd = new BufferedReader(new InputStreamReader(System.in));
    static StringBuilder sb;

    static void solve() throws Exception {
        String[] input = {};

        input = rd.readLine().split(" ");
        int n = Integer.parseInt(input[0]);

        input = rd.readLine().split(" ");
        int[] a = new int[n];
        long asum = 0;
        for (int i = 0; i < n; i++) {
            a[i] = Integer.parseInt(input[i]);
            asum += a[i];
        }

        input = rd.readLine().split(" ");
        int[] b = new int[n];
        long bsum = 0;
        for (int i = 0; i < n; i++) {
            b[i] = Integer.parseInt(input[i]);
            bsum += b[i];
        }

        // solution
        if (asum != bsum) {
            sb.append(-1 + "\n");
            return;
        }

        ArrayList<String> pairs = new ArrayList<>();
        for (int index = 0; index < n;) {
            if (a[index] != b[index]) {
                if (a[index] < b[index]) {
                    int j = index;
                    int i = -1;
                    for (int k = index + 1; k < n; k++) {
                        if (a[k] != b[k] && a[k] > b[k]) {
                            i = k;
                            break;
                        }
                    }
                    // System.out.println(i + " " + j + " if");
                    // displayArray(a);
                    a[i]--;
                    a[j]++;
                    pairs.add(i + " " + j + "\n");
                } else {
                    int i = index;
                    int j = -1;
                    for (int k = index + 1; k < n; k++) {
                        if (a[k] != b[k] && a[k] < b[k]) {
                            j = k;
                            break;
                        }
                    }
                    // System.out.println(i + " " + j + " else");
                    // displayArray(a);
                    a[i]--;
                    a[j]++;
                    pairs.add(i + " " + j + "\n");
                }
            }

            if (a[index] == b[index]) {
                index++;
            }
        }

        sb.append(pairs.size() + "\n");
        for (int i = 0; i < pairs.size(); i++) {
            sb.append(pairs.get(i));
        }
    }

    public static void main(String[] args) throws Exception {
        int test = Integer.parseInt(rd.readLine());
        sb = new StringBuilder();
        while (test-- > 0) {
            solve();
        }

        System.out.println(sb.toString());
    }

    static void displayArray(int[] arr) {
        for (int i = 0; i < arr.length; i++) {
            System.out.print(arr[i] + " ");
        }
        System.out.println();
    }
}

