import java.io.OutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.PrintWriter;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.ArrayList;
import java.util.StringTokenizer;
import java.io.BufferedReader;
import java.util.Collections;
import java.io.InputStream;

/**
 * Built using CHelper plug-in
 * Actual solution is at the top
 */
public class Main {
    public static void main(String[] args) {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        InputReader in = new InputReader(inputStream);
        PrintWriter out = new PrintWriter(outputStream);
        TaskD solver = new TaskD();
        solver.solve(1, in, out);
        out.close();
    }

    static class TaskD {
        int binSearch(ArrayList<El> ar, int diff, int pos) {
            int l = -1;
            int r = ar.size();
            while (r - l > 1) {
                int m = (r + l) / 2;
                El el = ar.get(m);
                if (el.diff < diff || (el.diff == diff && el.p <= pos)) {
                    l = m;
                } else {
                    r = m;
                }
            }
            if (r < 0 || r >= ar.size())
                return -1;
            if (ar.get(r).diff != diff || ar.get(r).p < pos)
                return -1;
            return r;
        }

        public void solve(int testNumber, InputReader in, PrintWriter out) {
            int n = in.nextInt();
            int w = in.nextInt();
            int h = in.nextInt();
            int[] g = new int[n];
            int[] p = new int[n];
            int[] t = new int[n];

            ArrayList<El> ver = new ArrayList<>();
            ArrayList<El> hor = new ArrayList<>();


            for (int i = 0; i < n; i++) {
                g[i] = in.nextInt();
                p[i] = in.nextInt();
                t[i] = in.nextInt();

                if (g[i] == 1) {
                    ver.add(new El(i, t[i] - p[i], g[i], p[i]));
                } else {
                    hor.add(new El(i, t[i] - p[i], g[i], p[i]));
                }
            }
            Collections.sort(ver);

            Collections.sort(hor);

            int[] xans = new int[n];
            int[] yans = new int[n];

            for (int i = 0; i < n; i++) {
                int idx = i;
                int ans;
                int pp = 0;
                while (true) {
                    ans = idx;
                    int prev = p[idx];
                    if (g[idx] == 1) {
                        idx = binSearch(hor, t[idx] - p[idx], pp);
                        if (idx == -1)
                            break;
                        idx = hor.get(idx).num;
                    } else {
                        idx = binSearch(ver, t[idx] - p[idx], pp);
                        if (idx == -1)
                            break;
                        idx = ver.get(idx).num;
                    }
                    pp = prev;
                }
                if (g[ans] == 1) {
                    yans[i] = h;
                    xans[i] = p[ans];
                } else {
                    yans[i] = p[ans];
                    xans[i] = w;
                }
            }

            for (int i = 0; i < n; i++) {
                out.println(xans[i] + " " + yans[i]);
            }
        }

        class El implements Comparable<El> {
            public int num;
            public int diff;
            public int g;
            public int p;

            public El(int num, int diff, int g, int p) {
                this.num = num;
                this.diff = diff;
                this.g = g;
                this.p = p;
            }


            public int compareTo(El o) {
                El x1 = this;
                El x2 = o;
                if (x1.diff == x2.diff)
                    return x1.p - x2.p;
                return x1.diff - x2.diff;
            }

        }

    }

    static class InputReader {
        StringTokenizer st;
        BufferedReader br;

        public InputReader(InputStream is) {
            BufferedReader br = new BufferedReader(new InputStreamReader(is));
            this.br = br;
        }

        public String next() {
            if (st == null || !st.hasMoreTokens()) {
                String nextLine = null;
                try {
                    nextLine = br.readLine();
                } catch (IOException e) {
                    throw new RuntimeException(e);
                }
                if (nextLine == null)
                    return null;
                st = new StringTokenizer(nextLine);
            }
            return st.nextToken();
        }

        public int nextInt() {
            return Integer.parseInt(next());
        }

    }
}

