int f(int i, int b)
{
    int res = b * a[i].c;
    if (m % b != 0)
    {
        int k = m / b;
        if (a[i].t + k > a[i].T)
            res += a[i].x * m;
        else if (a[i].t + k + 1 > a[i].T)
            res += a[i].x * (m % b) * (k + 1);
    }
    else
    {
        int k = m / b;
        if (a[i].t + k > a[i].T)
            res += k * a[i].x * b;
    }
    return res;
}

void solve()
{
    int ans = 0;
    for (int i = 0; i < n; ++i)
    {
        int l = 1, r = m;
        while (l + 5 < r)
