struct cld {
	ld x = 0, y = 0;
	cld() {}
	cld(ld x_) {
		x = x_;
	}
	cld(ld x_, ld y_) {
		x = x_, y = y_;
	}
	cld operator + (cld a) {
		return { x + a.x, y + a.y };
	}
	cld operator - (cld a) {
		return { x - a.x, y - a.y };
	}
	cld operator * (cld a) {
		return { x * a.x - y * a.y, x * a.y + y * a.x };
	}
};

const ll MAXN = 30;
vector<cld> as[MAXN];
ll ptr = 0;
void fft(vector<cld>& a, ll inv = 1) {
	ll n = sz(a);
	if (n <= 1) {
		return;
	}
	as[ptr++].assign(n / 2, 0);
	as[ptr++].assign(n / 2, 0);
	vector<cld>& a0 = as[ptr - 2];
	vector<cld>& a1 = as[ptr - 1];
	for (ll i = 0, j = 0; i < n; i += 2, j++) {
		a0[j] = a[i];
		a1[j] = a[i + 1];
	}
	fft(a0, inv);
	fft(a1, inv);
	cld w = 1;
	ld angle = 2.0 * pi * inv / ld(n);
	cld wn(cos(angle), sin(angle));
	for (ll i = 0; i < n / 2; i++) {
		a[i] = a0[i] + w * a1[i];
		a[i + n / 2] = a0[i] - w * a1[i];
		w = w * wn;
	}
	--ptr, --ptr;
}
vll mult(vll& A, vll& B) {
	ll n = 1;
	while (n < max(sz(A), sz(B))) {
		n *= 2;
	}
	vector<cld> a(n), b(n);
	for (ll i = 0; i < sz(A); i++) {
		a[i] = A[i];
	}
	for (ll i = 0; i < sz(B); i++) {
		b[i] = B[i];
	}
	fft(a);
	fft(b);
	for (ll i = 0; i < n; i++) {
		a[i] = a[i] * b[i];
	}
	fft(a, -1);
	for (cld& z : a) {
		z = z * (1.0 / ld(n));
	}
	vll ans(n);
	for (ll i = 0; i < n; i++) {
		ans[i] = round(a[i].x);
	}
	return ans;
}

