		lf[i].assign(n, -inf);
		rg[i].assign(n, inf);
	}
	lf[id(s[0])][0] = 0;
	rg[id(s[n - 1])][n - 1] = n - 1;
	for (ll i = 1; i < n; i++) {
		for (ll z = 0; z < 4; z++) {
