		for(int j = 0; j < m; j++){
			scanf("%d", &map[i][j]);
		}
	}

	//Process
	bool left, right;
	vector<int> up, down;
	up.assign(n+1, 0);
	down.assign(n+1, 0);
	for(int i = 0; i < n; i++){
		left = 0;
		for(int j = 0; j < m; j++){
			if(map[i][j]){
				up[j] = 1;
				left = 1;
			} else ans[i][j] += up[j]+left;
		}
	}
	for(int i = n-1; i >= 0; i--){
		right = 0;
		for(int j = m-1; j >= 0; j--){
			if(map[i][j]){
				down[j] = 1;
				right = 1;
			} else ans[i][j] += right+down[j];
		}
	}
	int totans = 0;
	for(int i = 0; i < n; i++){
		for(int j = 0; j < m; j++){
			totans += ans[i][j];
		}
	}

