int fastpow(int x, int y) {
    int answ = 1;

    while (y) {
        if (y & 1) {
            answ *= x;
        }

        x *= x;
        y >>= 1;
    }

    return answ;
}

int del(int a, int b) {
    return a * fastpow(b, mod - 2);
}

vector<int> gauss(vector<vector<int> > a) {
    int n = a.size();

    vector<int> answ(n);
    for (int i = 0; i < n; i ++) {
        for (int j = i + 1; j < n; j ++) {
            int domult = del(a[j][i], a[i][i]);
            for (int k = i; k <= n; k ++) {
                a[j][k] = (a[j][k] - (domult * a[i][i]) % mod + mod) % mod;
            }
        }
    }

    answ[n - 1] = del(a[n - 1][n], a[n - 1][n - 1]);
    for (int i = n - 2; i > -1; i --) {
        int left = a[i][n];
        for (int j = n - 1; j > i; j ++) {
            left -= a[i][j] * answ[j];
        }

        answ[i] = del(left, a[i][i]);
    }

    return answ;
}

