#include<bits/stdc++.h>
using namespace std; 
const int mod = 1e9 + 7;
const int N = 200000 + 10;
int power(int b, int exp){
    int res = 1;
    while(exp > 0){
        if(exp % 2){
            res = 1LL * res * b % mod;
        }
        b = 1LL * b * b % mod;
        exp /= 2;
    }
    return res;
}
long long seg[4 * N], best[4 * N], lazy[4 * N];
void update(int node, int start, int end, int l, int r, int val){
    if(lazy[node]){
        seg[node] += lazy[node];
        if(best[node] != 1e18){
            best[node] = seg[node];
        }
        if(start != end){
            lazy[node << 1] += lazy[node];
            lazy[node << 1 | 1] += lazy[node];
        }
        lazy[node] = 0;
    }
    if(start > end || r < start || l > end || l > r) return;
    if(l <= start && end <= r){
        seg[node] += val;
        if(best[node] != 1e18 || (l == r)){
            best[node] = seg[node];
        }
        if(start != end){
            lazy[node << 1] += val;
            lazy[node << 1 | 1] += val;
        }
        return;
    }
    int mid = (start + end) / 2;
    update(node << 1, start, mid, l, r, val);
    update(node << 1 | 1, mid + 1, end, l, r, val);
    best[node] = min(best[node << 1], best[node << 1 | 1]);
}
long long query(int node, int start, int end, int l, int r){
    if(lazy[node]){
        seg[node] += lazy[node];
        if(best[node] != 1e18){
            best[node] = seg[node];
        }
        if(start != end){
            lazy[node << 1] += lazy[node];
            lazy[node << 1 | 1] += lazy[node];
        }
        lazy[node] = 0;
    }
    if(start > end || r < start || l > end) return 1e18;
    if(l <= start && end <= r){
        return best[node];
    }
    int mid = (start + end) / 2;
    return min(query(node << 1, start, mid, l, r), query(node << 1 | 1, mid + 1, end, l, r));
}
int main(){
    int n, p[N], pos[N], a[N];
    scanf("%d", &n);
    for(int i = 0; i <= 4 * n; i++){
        best[i] = 1e18;
    }
    for(int i = 1; i <= n; i++){
        scanf("%d", &p[i]);
        pos[p[i]] = i;
    }
    for(int i = 1; i <= n; i++){
        scanf("%d", &a[i]);
        update(1, 1, n - 1, i, n - 1, a[i]);
    }
    long long ans = min(a[1], a[n]);
    for(int i = 1; i <= n; i++){
        update(1, 1, n - 1, 1, pos[i] - 1, a[pos[i]]);
        update(1, 1, n - 1, pos[i], n - 1, -a[pos[i]]);
        update(1, 1, n - 1, pos[i], pos[i], 0);
        ans = min(ans, query(1, 1, n - 1, 1, n - 1));
    }
    printf("%lld\n", ans);
    return 0;
}

