        for(int i=0;i<n;i++) s[i] = s();
        boolean flag = false;
        int max = Integer.MAX_VALUE,count=0,ans=0;

        int i,j,k,l;
        int m = s[0].length();
        for(i=0;i<n;i++)
        {
           char[] a = s[i].toCharArray();
           ans = 0;count = 0;
                flag = false;

           for(j=0;(j<n);j++)
           {
                if(j==i)
                    continue;
                char[] arr = s[j].toCharArray();


                for(k=0;k<m;k++)
                {
                    if(arr[k]!=a[k])
                        break;
                }
                if(k==m)
                {
                    continue;
                }
                for(k=0;k<m;k++)
                {
                    arr = leftrotate(arr,1,m);

                    for(l=0;l<m;l++)
                    {
                        if(arr[l]!=a[l])
                            break;
                    }
                    if(l==m)
                        count=k+1;
                }

                if(count!=0)
                    ans+=count;
                else
                {
                    flag = true;
                    break;
                }

           }

           if(flag)
                break;
            else
            {
                max = min(max,ans);
            }

        }
        if(flag)
            out.println("-1");
        else
            out.println(max);
        out.close();
    }
    static char[] leftrotate(char[] arr,int d,int n)
    {
        int i,j,k,temp;
        for(i=0;i<gcd(d,n);i++)
        {
            temp = (int)arr[i] ;
            j = i;
            while(true)
            {
                k = j+d;
                if(k>=n)
                    k = k-n;
                if(k == i) break;
                arr[j] = arr[k];
