        if (com.android.mms.LogTag.VERBOSE)
            com.android.mms.ui.ComposeMessageActivity.log(("bail due to resultCode=" + resultCode));

        return ;
    }
    switch (requestCode) {
        case com.android.mms.ui.ComposeMessageActivity.REQUEST_CODE_CREATE_SLIDESHOW :
            if (data != null) {
                com.android.mms.data.WorkingMessage newMessage = com.android.mms.data.WorkingMessage.load(this, data.getData());
