                android.net.Uri uri = android.net.Uri.fromFile(file);
                com.android.mms.MmsApp.getApplication().getThumbnailManager().removeThumbnail(uri);
                addImageAsync(uri, append);
                break;
            }
        case com.android.mms.ui.ComposeMessageActivity.REQUEST_CODE_ATTACH_IMAGE :
            {
                if (data != null) {
                    addImageAsync(data.getData(), append);
                }
                break;
            }
        case com.android.mms.ui.ComposeMessageActivity.REQUEST_CODE_TAKE_VIDEO :
            android.net.Uri videoUri = com.android.mms.TempFileProvider.renameScrapFile(".3gp", null, this);
            com.android.mms.MmsApp.getApplication().getThumbnailManager().removeThumbnail(videoUri);
            addVideoAsync(videoUri, append);
            break;
        case com.android.mms.ui.ComposeMessageActivity.REQUEST_CODE_ATTACH_VIDEO :
            if (data != null) {
                addVideoAsync(data.getData(), append);
            }
            break;
        case com.android.mms.ui.ComposeMessageActivity.REQUEST_CODE_ATTACH_SOUND :
            {
                android.net.Uri uri = ((android.net.Uri) (data.getParcelableExtra(RingtoneManager.EXTRA_RINGTONE_PICKED_URI)));
                if (uri == null) {
                    uri = data.getData();
                }else
                    if (Settings.System.DEFAULT_RINGTONE_URI.equals(uri)) {
                        break;
                    }

                addAudio(uri, append);
                break;
            }
        case com.android.mms.ui.ComposeMessageActivity.REQUEST_CODE_RECORD_SOUND :
            if (data != null) {
                addAudio(data.getData(), append);
            }
            break;
        case com.android.mms.ui.ComposeMessageActivity.REQUEST_CODE_ECM_EXIT_DIALOG :
            boolean outOfEmergencyMode = data.getBooleanExtra(com.android.mms.ui.ComposeMessageActivity.EXIT_ECM_RESULT, false);
            if (outOfEmergencyMode) {
                sendMessage(false);
            }
            break;
        case com.android.mms.ui.ComposeMessageActivity.REQUEST_CODE_PICK :
            if (data != null) {
                processPickResult(data);
            }
            break;
        case com.android.mms.ui.ComposeMessageActivity.REQUEST_CODE_ATTACH_REPLACE_CONTACT_INFO :
            if (data != null) {
                mWorkingMessage.removeAttachment(true);
            }
        case com.android.mms.ui.ComposeMessageActivity.REQUEST_CODE_ATTACH_ADD_CONTACT_INFO :
            if (data != null) {
                java.lang.String newText = (mWorkingMessage.getText()) + (data.getStringExtra(SelectRecipientsList.EXTRA_INFO));
                mWorkingMessage.setText(newText);
            }
            break;
        case com.android.mms.ui.ComposeMessageActivity.REQUEST_CODE_ATTACH_ADD_CONTACT_VCARD :
            if (data != null) {
                java.lang.String extraVCard = data.getStringExtra(SelectRecipientsList.EXTRA_VCARD);
                if (extraVCard != null) {
                    android.net.Uri vcard = android.net.Uri.parse(extraVCard);
                    addVcard(vcard);
                }
            }
            break;
        case com.android.mms.ui.ComposeMessageActivity.REQUEST_CODE_ADD_RECIPIENTS :
            mAddNumbersTask = new com.android.mms.ui.ComposeMessageActivity.AddNumbersTask();
            mAddNumbersTask.execute(data.getStringArrayListExtra(SelectRecipientsList.EXTRA_RECIPIENTS));
            break;
        case com.android.mms.ui.ComposeMessageActivity.REQUEST_CODE_ADD_CALENDAR_EVENTS :
            if (data != null) {
                java.util.ArrayList<android.net.Uri> uris = data.getParcelableArrayListExtra(Intent.EXTRA_STREAM);
                if ((uris.size()) > 0) {
                    addVCal(uris.get(0));
                }
            }
        default :
            if (com.android.mms.LogTag.VERBOSE)
                com.android.mms.ui.ComposeMessageActivity.log(("bail due to unknown requestCode=" + requestCode));

            break;
    }
}

