using namespace std;
int n, m , mod ;
long long dp[2][555][555] ;
char c[11111] ;
int lie[555] ;
double C[555][555] ;
int main()
{
    cin>>n>>m>>mod ;
    long long i,j,k ;
    for(i = 1;i < 555;i++){
        C[i][0] = C[i][i] = 1.0;
        for(j = 1;j < i;j++)
            C[i][j] = C[i-1][j] + C[i-1][j-1];
    }
    for(i=1;i<=m;i++)
    {
        scanf("%s",c) ;
        for(j=0;j<n;j++)
        {
            if(c[j]=='1')
            {
                lie[j] ++ ;
            }
        }
    }
    int one = 0 ,zero = 0 ;
    for(i=0;i<n;i++)
    {
        if(lie[i]==0)  zero ++ ;
        else if(lie[i]==1)   one ++ ;
    }
    dp[m%2][zero][one] = 1 ;
    int t ,p;
    for(i=m+1;i<=n;i++)
    {
        if(i%2==1)  t=1,p=0 ;
        else  t=0,p=1 ;
        for(j=0;j<n;j++)
        {
            for(k=0;k<n;k++)
            {
                if(k+j<=n&&j+2<=n&&k-2>=0)
                {
                    dp[t][j][k] += dp[p][j+2][k-2]*(((j+2)*(j+1)/2)%mod)%mod ;
                    dp[t][j][k] %= mod ;
                }
                if(j+k+2<=n)
                {
                    dp[t][j][k] += dp[p][j][k+2]*((k+1)*(k+2)/2%mod)%mod ;
                    dp[t][j][k] %= mod ;
                }
