    for(int i = 0;i < m  ; ++i)
    {
        int a, b;
        cin >> a >> b;
        --a;--b;
        G[a].push_back(b);
        G[b].push_back(a);
    }

    /// star
    int ok = 0;
    for(int i = 0; i < n; ++i)
    {
        if(G[i].size() == 1 || G[i].size() == (n - 1))
            ok++;
    }
    if(ok == n)
    {
        cout << "star topology" << endl;
        return 0;
    }

    // ring
    dfs(0);
    if(all == n)
    {
        int ok = 0;
        for(int i = 0; i < n; ++i)
        {
            if(G[i].size() == 2)
                ok++;
        }
        if(ok == n)
        {
            cout << "ring topology" << endl;
            return 0;
        }
    }

    // bus
    ok = 0;
    int ok2 = 0;
    for(int i = 0; i < n; ++i)
    {
        if(G[i].size() == 2)
            ok++;
        else if(G[i].size() == 1)
            ok2++;
    }

    if(ok2 == 2 && ok == n - 2)
    {
        cout << "bus topology" << endl;
        return 0;
    }

    cout << "unknown topology" << endl;

    return 0;
}

