#define IOS ios::sync_with_stdio(0); cin.tie(0); cout.tie(0);
//#define endl "\n"
#define int long long

const int N=1e5+5;

int n, m, q;
vector<int> g[N];
int lvl[N];
int diaend1=0, maxd1=0, maxdia=0, diaend2=0;
int vis[N], maxdist[N], cmp[N];
vector<int> each_tree_dist[N], pre_each_tree_dist[N];
int cnt=0;

void dfs(int k, int lvl)
{
	vis[k] = 1;
	for(auto &it:g[k])
	{
		if(!vis[it])
			dfs(it,lvl+1);
	}
	if(lvl>maxd1)
	{
		maxd1=lvl;
		diaend1=k;
	}
}

void dfs2(int k, int par, int dist)
{
	for(auto &it:g[k])
	{
		if(it!=par)
		{
			maxdist[it]=max(maxdist[it],dist+1);
			dfs2(it,k,dist+1);
		}
	}
	if(maxdist[k]>maxdia)
	{
		maxdia=maxdist[k];
		diaend2=k;
	}
}

void dfs3(int k, int par)
{
	cmp[k]=cnt;
	for(auto &it:g[k])
	{
		if(it!=par)
		{
			dfs3(it,k);
		}
	}
	each_tree_dist[cnt].push_back(maxdist[k]);
}

int32_t main()
{	
	IOS;
	cin>>n>>m>>q;
	for(int i=0;i<m;i++)
	{
		int u, v;
		cin>>u>>v;
		g[u].push_back(v);
		g[v].push_back(u);
	}

	for(int i=1;i<=n;i++)
	{
		if(!vis[i])
		{
			diaend1=0, maxd1=0, maxdia=0, diaend2=0;
			dfs(i,0);
			dfs2(diaend1,0,0);
			dfs2(diaend2,0,0);
			dfs3(i,0);
			cnt++;
		}	
	}
	for(int i=0;i<cnt;i++)
	{
		sort(each_tree_dist[i].begin(), each_tree_dist[i].end());
		int j=0;
		for(auto &it:each_tree_dist[i])
		{
			pre_each_tree_dist[i].push_back( (j!=0?pre_each_tree_dist[i][j-1]:0) + it );
			j++;
		}
	}
	while(q--)
	{
		int u, v;
		cin>>u>>v;
		int cmp1=cmp[u], cmp2=cmp[v];
		if(cmp1==cmp2)
		{
			cout<<"-1\n";
			continue;
		}
		int max_query_dia=max(each_tree_dist[cmp1].back(),each_tree_dist[cmp2].back());
		if(u<v)
			swap(v,u);
		int n=each_tree_dist[cmp1].size();
		int m=each_tree_dist[cmp2].size();
		if(n<m)
		{
			swap(n,m);
			swap(cmp1,cmp2);
		}
		double ans=0;
		for(auto &it : each_tree_dist[cmp2])
		{
			int req_min = max_query_dia - it - 1;
			int in = lower_bound(each_tree_dist[cmp1].begin(),each_tree_dist[cmp1].end(),req_min) - each_tree_dist[cmp1].begin();
			if(in == 0)
			{
				ans += pre_each_tree_dist[cmp1].back() + (it+1)*n ;
			}
			else
			{
				ans += pre_each_tree_dist[cmp1].back() - pre_each_tree_dist[cmp1][in-1] + (in)*max_query_dia + (n-in)*(it+1);
			}
		}
		cout<<fixed<<setprecision(12)<<ans/(1.0l*n*m)<<endl;
	}	
	return 0;
}

