#define size(x) (!x ? 0 : x->size)
struct Node {
	Node *l, *r, *par, *orig;
	int fix, size, val;
	Node() { }
	Node(int val, Node *l = NULL, Node *r = NULL, Node *par = NULL, Node *orig = NULL)
	: l(l), r(r), fix(rand()), val(val), par(par), orig(orig) { size = 1; }
	inline void pushup() {
		size = size(l) + size(r) + 1;
	}
} *rt[100005], *root;
Node *merge(Node *l, Node *r) {
	if (!l || !r) return l ? l : r;
	if (l->fix < r->fix) {
		l->r = merge(l->r, r);
		if (l->r) l->r->par = l;
		l->pushup();
		return l;
	}
	else {
		r->l = merge(l, r->l);
		if (r->l) r->l->par = r;
		r->pushup();
		return r;
	}
}
void split(Node *rt, int sz, Node *&l, Node *&r) {
	if (!rt) l = r = NULL;
	else {
		if (size(rt->l) < sz) {
			l = rt;
			split(l->r, sz - size(rt->l) - 1, l->r, r);
			if (l->r) l->r->par = l;
			l->pushup(); 
		}
		else {
			r = rt;
			split(r->l, sz, l, r->l);
			if (r->l) r->l->par = r;
			r->pushup(); 
		}
	}
}
inline int get_rank(Node *rt) {
	bool is_right = true;
	int ans = 0;
	while (rt) {
		if (is_right) ans += size(rt->l) + 1;
		is_right = (rt->par && rt->par->r == rt);
		rt = rt->par;
	}
	return ans;
}

inline int find_index(Node *rt, int k) {
	int ans = 0;
	while (rt)
		if (get_rank(rt->orig) <= k)
			ans += size(rt->l) + 1, rt = rt->r;
		else rt = rt->l;
	return ans;
}
