#include <cstdio>
#include <vector>
#include <ctime>
#include <algorithm>
using namespace std;

const int Maxm = 200000 + 20;
vector <int> vec[Maxm];
int ans[100][100], a[Maxm],c[Maxm], cd[Maxm];
int n, m, k, Ans;
int Std;

void check()
{
	int t = 0;
	for(int i = 1; i <= k; i++)
		t += ans[i][c[i]];
	if(t == Std)	Ans++;
}
void dfs(int x)
{
	if(x > k)
	{
		check();
		return;
	}
	for(int i = 1; i <= x; i++)
	{
		c[x] = i;
		dfs(x+1);
	}
}
int main()
{
	cin >> n >> m >> k;
	for(int i = 1; i <= n; i++)
	{
		a[i] = rand();
		Std += a[i];
	}
	for(int i = 1; i <= m; i++)
	{
		int u, v, w;
		scanf("%d%d%d", &u, &v, &w);
		vec[u].push_back(v);
		cd[u]++;
	}
	for(int i = 1; i <= n; i++)
		if((int)vec[i].size() > 0)
			sort(vec[i].begin(), vec[i].end());
	for(int i = 1; i <= n; i++)
		for(int j = 0; j < cd[i]; j++)
			ans[cd[i]][j+1] += a[vec[i][j]];
	dfs(1);
	cout << Ans << endl;
	return 0;
}

