#include <iostream>
#include <vector>
#include <utility>
#include <cmath>
#include <set>
#include <map>
#include <algorithm>
#include <iomanip>
#include <cstdio>
using namespace std;
const int MAXN = 500005;
const long long modulo = 1000000007;
const int inf = 1000000001;

int tIn[MAXN], tOut[MAXN] , level[MAXN], V[MAXN], cnt[30][MAXN];
vector < int > p[MAXN];
int timer;

void DFS( int v )
{
    timer++; tIn[v] = timer;

    for ( int i = 0; i < p[v].size(); i++)
        DFS( p[v][i] );

    timer++; tOut[v] = timer;
}

bool compareV( int x , int y )
{
    if ( level[x] < level[y] ) return true;
    if ( level[x] > level[y] ) return false;
    return tIn[x] < tIn[y];
}

int start[MAXN];
char z[MAXN];

int main()
{
    //freopen("input.in" , "r" , stdin);

    int N , M;
    scanf("%d%d", &N, &M);

    level[1] = 1;
    for (int i = 2; i <= N; i++)
    {
        int x;
        scanf("%d", &x);
        p[x].push_back( i );
        level[i] = level[x] + 1;
    }

    scanf("%s", z );
    scanf("%s", z );

    timer = 0;
    DFS( 1 );

    for (int i = 1; i <= N; i++)
        V[i] = i;

    sort( V + 1 , V + N + 1 , compareV);
    V[N + 1] = N + 1;
    level[N + 1] = inf;

    int Lvl = 0;
    for (int i = 1; i <= N; i++)
    {
        for ( int j = 0; j < 30; j++)
        {
            cnt[j][i] = cnt[j][i-1];
        }

        cnt[ int( z[ V[i] - 1 ] ) - int('a') ][i]++;

        if ( Lvl != level[ V[i] ] )
        {
            Lvl = level[ V[i] ];
            start[ Lvl ] = i;
        }
    }
    start[Lvl + 1] = N + 1;

    for (int i = 1; i <= M; i++)
    {
        int v , h;
        cin >> v >> h;

        if ( h == 1 || h > Lvl)
        {
            printf("Yes\n");
            continue;
        }

        int l = start[h] - 1;
        int r = start[h + 1];

        while (r - l > 1)
        {
            int mid = (l + r) / 2;
            int u = V[mid];
            if ( level[u] == h)
            {
                if ( tIn[u] < tIn[v] )
                    l = mid;
                else
                    r = mid;
            }
        }

        int GlobalL = r;

        l = GlobalL;
        r = start[h + 1];

        while (r - l > 1)
        {
            int mid = (l + r) / 2;
            int u = V[mid];
            if ( level[u] == h)
            {
                if ( tIn[u] > tOut[v] )
                    r = mid;
                else
                    l = mid;
            }
        }

        int GlobalR = l;
        //cout << GlobalL << " " << GlobalR << endl;
        if ( GlobalR < GlobalL )
        {
            printf("Yes\n");
        }
        else
        {
            int odds = 0;
            for (int j = int('a'); j <= int('z'); j++)
            {
                if ( (cnt[j - int('a')][GlobalR] - cnt[j - int('a')][GlobalL - 1]) % 2 == 1 ) odds++;
            }

            if ( odds < 2 )
                printf("Yes\n");
            else
                printf("No\n");
        }
    }


    return 0;
}

