#include<bits/stdc++.h>
using namespace std;
#define ll long long
#define pb push_back
#define mp make_pair
#define ff first
#define ss second
#define MAX 200000

vector<int>graph[MAX];
vector<int>topo;
int parent[MAX];
int gifts[MAX];
int visit[MAX];
int send[MAX];
int INC[MAX];
vector<int>answer;

void toposort(int index)
{
  int i;
  if (visit[index]==0)
  {
    visit[index]=1;
    for(i=0;i<graph[index].size();i++)
    {
      toposort(graph[index][i]);
    }
  }
  topo.pb(index);
}

void gifting(int index,int ancestor)
{
  int i;
  if (send[index]!=-1)
  return ;
  else
  {
    send[index]=ancestor;
    for(i=0;i<graph[index].size();i++)
    {
      gifting(graph[index][i],ancestor);
    }
  }
}

int main()
{
  int n,m;int i;
  cin>>n>>m;
  int p,q;
  for(i=0;i<MAX;i++)
  {
    visit[i]=0;
    send[i]=-1;
    INC[i]=0;
  }
  for(i=0;i<m;i++)
  {
    cin>>p>>q;
    graph[p].pb(q);
  }
  for(i=1;i<=n;i++)
  {
    cin>>gifts[i];
    INC[gifts[i]]=1;
  }
  for(i=1;i<=n;i++)
  {
    if (!visit[i])
    {
      toposort(i);
    }
  }

  for(i=0;i<=topo.size();i++)
  {
    if (INC[topo[i]])
    {
      gifting(topo[i],topo[i]);
      answer.pb(topo[i]);
    }
  }
  for(i=1;i<=n;i++)
  {
    if (gifts[i]!=send[i])
    {
      cout<<"-1\n";
      return 0;
    }
  }

  cout<<answer.size()<<"\n";
  for(i=0;i<answer.size();i++)
  {
    cout<<answer[i]<<"\n";
  }

return 0;
}//main

