#include <iostream>
#include <map>
#include <string>
#include <limits>

struct Player
{
    Player(): score(0), round_id(0){}
    int score;
    int round_id;
};

int main(int argc, char *argv[])
{
    int round_count = 0;
    std::cin>>round_count;

    std::map<std::string, Player> board;
    std::string name;
    int score;
    for (int i = 0; i < round_count; i++)
    {
        std::cin>>name>>score;
        if (board.find(name)!=board.end())
        {
            board[name].score += score;
            board[name].round_id = i;
        }
        else
        {
            board[name].score = score;
            board[name].round_id = i;
        }
    }
    std::string leader = "";
    int best_score = std::numeric_limits<int>::min();
    int best_round_id = std::numeric_limits<int>::min();
    for (auto it = board.begin(); it != board.end(); it++)
    {
        if(it->second.score > best_score)
        {
            best_score = it->second.score;
            best_round_id = it->second.round_id;
            leader = it->first;
        }
        else if (it->second.score == best_score && it->second.round_id < best_round_id)
        {
            best_score = it->second.score;
            best_round_id = it->second.round_id;
            leader = it->first;
        }
    }
    std::cout<<leader<<std::endl;

    return 0;
}

