#include <iostream>
#include <map>
using namespace std;

#define MOD 1000000007
#define MP make_pair
typedef pair<int, int > pii;
typedef pair<pii, pii> ppp;
typedef map<ppp, int> mpppi;
char input[500][500];
mpppi output1;
mpppi output2;
int N, M;

void read_help(int M, int N, int p) {
  for (int i = 0; i < M; ++i) {
    for (int j = 0; j < N; ++j) {
      cin >> input[(1-p)*i+p*j][(1-p)*j+p*i];
    }
  }
}

void read_input() {
  cin >> M >> N;
  if (M > N) {
    swap(M, N);
    read_help(N, M, 1);    
    return;
  }
  read_help(M, N, 0);
}

int solve() {
 int L = (M + N - 2) >> 1;
 for (int i = L; i >= 0; --i) {
   output1 = output2;
   output2.clear();
   int k = (i < M - 1 ? i : M - 1);
   int j = (i < M - 1 ? 0 : i - M + 1);
   for (; k >= 0; --k, ++j) {
     int k1 = M - 1;
     int j1 = N - i - 1;
     for (; j1 <= N - 1; ++j1, --k1) {
       if (k1 >= k && j1 >= j && input[k][j] == input[k1][j1]) {
         ppp cur = MP(MP(k, j), MP(k1, j1));
         pii t1 = MP(k, j + 1);
         pii t2 = MP(k+1, j);
         pii t3 = MP(k1-1, j1);
         pii t4 = MP(k1, j1-1);
         if ((k1 == k && j1 - j <= 1) || (k1 - k <= 1 && j1 == j)) {
           output2[cur] = 1;
         } else {
           output2[cur] = (output2[cur] + output1[MP(t1, t3)]) % MOD;
           output2[cur] = (output2[cur] + output1[MP(t2, t4)]) % MOD;
           output2[cur] = (output2[cur] + output1[MP(t1, t4)]) % MOD;
           output2[cur] = (output2[cur] + output1[MP(t2, t3)]) % MOD;
         }
       }
     }
   }
 }
 return output2[MP(MP(0,0), MP(M-1, N-1))];        
} 


int main() {
  read_input();
  cout << solve() << '\n';
  return 0;
}

