        Q.pop();
        inQ[nod] = false;

        for (auto it : v[nod])
        {
            if (!fw[nod][it])
                continue;
            if (mn[it] <= c[nod][it] + mn[nod])
            {
                mn[it] = c[nod][it] + mn[nod];
                if (!inQ[it])
                    Q.push(it),inQ[it] = true;
            }
        }
    }
}

bool dijkstra()
{
