bool is_feasible(int A[MAXN][MAXN]) {
	for (int i = 0; i < n; i++) {
		for (int j = 1; j < m; j++) {
			if (A[i][j] <= A[i][j - 1]) {
				return false;
			}
		}
	}

	for (int j = 0; j < m; j++) {
		for (int i = 1; i < n; i++) {
			if (A[i][j] < A[i - 1][j]) {
				return false;
			}
		}
	}

	return true;
}

