inline int read(){
	char c=getchar();int t=0,f=1;
	while((!isdigit(c))&&(c!=EOF)){if(c=='-')f=-1;c=getchar();}
	while((isdigit(c))&&(c!=EOF)){t=(t<<3)+(t<<1)+(c^48);c=getchar();}
	return t*f;
}
int n,k,a[maxn];
char s[7];
const int alfa[10]={119,18,93,91,58,107,111,82,127,123};
string f[maxn][maxn];
inline int deal(int x){
	int tmp=0;
	for(int i=0;i<7;i++){
		if(x&(1<<i))tmp++;
	}
	return tmp;
}
signed main(){
	//freopen("2.in","r",stdin);
	n=read();k=read();
	for(int i=1;i<=n;i++){
		scanf("%s",s);
		int tmp=0;
		for(int j=0;j<7;j++){
			tmp=(tmp<<1)+(s[j]-'0');
		}
		a[i]=tmp;
	}
	for(int i=0;i<=9;i++){
		if((alfa[i]^(alfa[i]|a[1]))==0){
			int tmp=deal(alfa[i]^a[1]);
			char c=i+'0';
			string qaq;
			qaq.clear();
			qaq.push_back(c);
			f[1][tmp]=max(f[1][tmp],qaq);
			//cout<<f[1][tmp]<<' '<<tmp<<endl;
		}
	}
	for(int i=2;i<=n;i++){
		for(int j=0;j<=k;j++){
			for(int u=0;u<=9;u++){
				if((alfa[u]^(alfa[u]|a[i]))==0){
					int tmp=deal(alfa[u]^a[i]);
					if(tmp>j)continue;
					char c=u+'0';
					if(f[i-1][j-tmp].empty())continue;
					string qaq;qaq.clear();
					qaq.push_back(c);
					if(!f[i][j].empty())
					f[i][j]=max(f[i][j],f[i-1][j-tmp]+qaq);
					else
					f[i][j]=f[i-1][j-tmp]+qaq;
				}
			}
		}
	}
	if(!f[n][k].empty())
	cout<<f[n][k]<<endl;
	else
	puts("-1");
	return 0;
}

