using namespace std;

int countup(int a, int b, int gcd) {
	int count = 0;

	while (a % gcd != 0 && b % gcd != 0) {
		a++;
		b++;
		count++;
	}

	return count;
}

int countdown(int a, int b, int gcd) {
	int count = 0;

	while (a % gcd != 0 && b % gcd != 0) {
		a--;
		b--;
		count++;
	}

	return count;
}

