using namespace std;
const int N=2e5+100;
ll ans[N];
ll cnt[N];
vector <int> p[N];
ll mod=1e9+7;
ll solve(vector <int> a){
    vector <int> b=a;
    for (auto u : a){
        cnt[u]++;
        for (auto v : p[u]){
            b.pb(v);
            cnt[v]++;
        }
    }
    sort(b.begin(),b.end());
    b.resize(unique(b.begin(),b.end())-b.begin());
    ll jav=0;
    for (auto u : b){
        cnt[u]=cnt[u]*(cnt[u]-1)+cnt[u];
    }
    reverse(b.begin(),b.end());
    for (auto u : b){
        for (auto v : p[u]){
            cnt[v]-=cnt[u];
        }
        jav+=cnt[u]*u%mod;
        jav%=mod;
    }

    for (auto u : b){
        cnt[u]=0;
    }
    return jav;

}
