using namespace std;
typedef long long ll;

struct DATA {
    ll mmin, mmax;
    int pos;
    DATA(ll _mmin, ll _mmax, int _pos) : mmin(_mmin), mmax(_mmax), pos(_pos) {}
    bool operator < (const DATA& rhs) const {
        return mmax==rhs.mmax ? mmin<rhs.mmin:mmax<rhs.mmax;
    }
};

int n, m, sol[200010];
ll d, l[200010], r[200010];
