    scanf("%d%d", &n, &m);
    for(int i=0 ; i<n ; i++) {
        scanf("%I64d%I64d", &l[i], &r[i]);
        if(i) vd.pb(DATA(l[i]-r[i-1], r[i]-l[i-1], i-1));
    }
    sort(vd.begin(), vd.end());

    for(int i=0 ; i<m ; i++) {
        scanf("%I64d", &d);
        pq.push(mp(-d, i+1));
    }

    for(int i=0 ; i<vd.size() ; i++) {
        while(pq.size() && -pq.top().first<vd[i].mmin) pq.pop();
