#include <bits/stdc++.h>

using namespace std;

const int MAXN = (1 << 20) + 10;
const int MAXM = 20 + 10;
const int INF = (int)(1e9);
const int MOD = (int)(1e9) + 7;

int a[MAXN], b[MAXN];
long long S[MAXM], I[MAXM];
int n, m;

void process(int depth, int l, int r) {
    if (depth == 0) return;
    int mid = l + (1 << (depth - 1)) - 1;
    process(depth - 1, l, mid);
    process(depth - 1, mid + 1, r);
    for(int i = l; i <= r; i++) b[i] = a[i];
    int i1 = l, i2 = mid + 1;
    for(int i = l; i <= r; i++) {
        if ((i1 <= mid) && ((i2 > r) || (b[i1] < b[i2]))) {
            a[i] = b[i1]; i1++;
        }
        else {
            a[i] = b[i2]; i2++;
        }
    }

    long long inv = 0;
    int j = mid + 1;
    for(int i = l; i <= mid; i++) {
        while ((j < r) && (b[j + 1] < b[i])) j++;
        if (b[j] < b[i]) inv += (j - mid);
    }
    j = r;
    long long s = 0;
    for(int i = mid; i >= l; i--) {
        while ((j > mid + 1) && (b[j - 1] > b[i])) j--;
        if (b[j] > b[i]) s += (r - j + 1);
    }
    S[depth] += s;
    I[depth] += inv;
}

long long solve() {
    int q;
    cin >> q;
    for(int i = 1; i <= q; i++) {
        swap(I[i], S[i]);
    }

    long long res = 0;
    for(int i = 1; i <= n; i++) res += I[i];
    return res;
}

int main()
{
    //freopen("414C.in", "r", stdin);

    cin >> n;
    for(int i = 1; i <= (1 << n); i++) scanf("%d", &a[i]); //cin >> a[i];
    process(n, 1, (1 << n));
    cin >> m;
    for(int i = 1; i <= m; i++) {
        printf("%I64d\n", solve());
        //cout << solve_2(A) << endl;
    }
}

