    } catch (java.lang.Throwable t) {
        com.ogarproject.ogar.server.OgarServer.log.log(java.util.logging.Level.SEVERE, "Failed to load plugins", t);
    }
    com.ogarproject.ogar.server.OgarServer.log.info("Enabling plugins.");
    pluginManager.enablePlugins();
    networkManager = new com.ogarproject.ogar.server.net.NetworkManager(this);
