        if (com.ogarproject.ogar.server.gui.ServerGUI.isSpawned()) {
            java.lang.System.exit(1);
        }else {
            java.lang.System.exit(1);
        }
    }
    tickWorkers.forEach(TickWorker::start);
    running = true;
    while (running) {
        try {
            long startTime = java.lang.System.currentTimeMillis();
            (tick)++;
            world.tick(this::tick);
            for (com.ogarproject.ogar.server.world.PlayerImpl player : playerList.getAllPlayers()) {
                tick(player.getTracker()::updateNodes);
            }
            tickWorkers.forEach(TickWorker::waitForCompletion);
            scheduler.serverTick(tick);
            long tickDuration = (java.lang.System.currentTimeMillis()) - startTime;
            if (tickDuration < 50) {
                com.ogarproject.ogar.server.OgarServer.log.finer((("Tick took " + tickDuration) + "ms, sleeping for a bit"));
                java.lang.Thread.sleep((50 - tickDuration));
            }else {
                com.ogarproject.ogar.server.OgarServer.log.finer((("Tick took " + tickDuration) + "ms (which is >=50ms), no time for sleep"));
            }
        } catch (java.lang.InterruptedException ex) {
            break;
        }
    } 
    tickWorkers.forEach(TickWorker::shutdownGracefully);
    tickWorkers.forEach(TickWorker::waitForShutdown);
    networkManager.shutdown();
    com.ogarproject.ogar.server.OgarServer.log.info("Disabling plugins...");
    pluginManager.disablePlugins();
    com.ogarproject.ogar.server.OgarServer.log.info("Successfully stopped server!");
    try {
        java.lang.Thread.sleep(1500);
    } catch (java.lang.InterruptedException e) {
        e.printStackTrace();
    }
    java.lang.System.exit((-1));
}

