using namespace std;
typedef long long i64;

char grid[100][101];
int m, n;

void AddElement(int i, int j, vector<char>& v) {
  if (i >= 0 && i < m && j >= 0 && j < n && islower(grid[i][j]))
    v.push_back(grid[i][j]);
}
char GetNewColor(const vector<char>& v) {
  for (int i = 0; i < (int)v.size(); ++i)
    if (v[i] != i + 'a') return i + 'a';
  return (int)v.size() + 'a';
}
void PlacePiece(int i, int j, int r, int c) {
  vector<char> v;
  for (int ii = i - 1, jj = j; jj < j + c; ++jj) AddElement(ii, jj, v);
  for (int ii = i + r, jj = j; jj < j + c; ++jj) AddElement(ii, jj, v);
  for (int ii = i, jj = j - 1; ii < i + r; ++ii) AddElement(ii, jj, v);
  for (int ii = i, jj = j + c; ii < i + r; ++ii) AddElement(ii, jj, v);
  sort(v.begin(), v.end());
  v.erase(unique(v.begin(), v.end()), v.end());
  const char cc = GetNewColor(v);
  for (int ii = i; ii < i + r; ++ii)
    for (int jj = j; jj < j + c; ++jj)
      grid[ii][jj] = cc;
}
int main() {
  scanf("%d %d", &m, &n);
  int a, b, c; scanf("%d %d %d", &a, &b, &c);
  if ((m * n) % 2 == 1) printf("%s\n", "IMPOSSIBLE");
  else {
    for (int i = 0; i < m; ++i) {
      for (int j = 0; j < n; ++j) grid[i][j] = '.';
      grid[i][n] = '\0';
    }
    bool ok = true;
    if (ok && m % 2 == 1) {
      ok = (b -= n / 2) >= 0;
      if (ok) for (int j = 0, k = 0; j < n; j += 2, k ^= 1)
        grid[m - 1][j] = grid[m - 1][j + 1] = k + 'a';
    }
    if (ok && n % 2 == 1) {
      ok = (a -= m / 2) >= 0;
      if (ok) for (int i = 0, k = 0; i < m; i += 2, k ^= 1)
        grid[i][n - 1] = grid[i + 1][n - 1] = k + 'a';
    }
    for (int i = 0; i + 2 <= m && ok; i += 2)
      for (int j = 0; j + 2 <= n && ok; j += 2)
        if (c >= 1) PlacePiece(i, j, 2, 2), --c;
        else if (b >= 2) PlacePiece(i, j, 2, 1), PlacePiece(i, j + 1, 2, 1), b -= 2;
        else if (a >= 2) PlacePiece(i, j, 1, 2), PlacePiece(i + 1, j, 1, 2), a -= 2;
        else ok = false;
    if (ok) for (int i = 0; i < m; ++i) printf("%s\n", grid[i]);
    else printf("%s\n", "IMPOSSIBLE");
  }
  return 0;
}

