{
    for(int i=1;i<=2*n;i+=2){
        swap(a[i],a[i+1]);
    }
}
void op2()
{
    for(int i=1;i<=n;i++){
        swap(a[i],a[i+n]);
    }
}
bool sorted(){
    for(int i=2;i<=2*n;i++){
        if(a[i-1]>a[i]){
            return false;
        }
    }
    return true;
}
int main(){
    //int n;
    cin>>n;
    for(int i=1;i<=2*n;i++){
        cin>>a[i];
    }
    if(sorted()){
        cout<<0<<endl;
        return 0;
    }
    op1();
    if(sorted()){
        cout<<1<<endl;
        return 0;
    }else{
        op1();
        op2();
        if(sorted()){
            cout<<1<<endl;
            return 0;
        }
        op2();
        op1();
    }
    op2();
    if(sorted()){
        cout<<2<<endl;
        return 0;
    }
    op1();
    if(sorted()){
        cout<<3<<endl;
        return 0;
    }
    cout<<-1<<endl;



}

