        nsiStatus = ((response.substring((indx0 + (patterns[0].length())), indx1)) + (response.substring((indx1 + (patterns[1].length())), indx2))) + (response.substring((indx2 + (patterns[2].length())), indx3));
    }
    return nsiStatus;
}

