#include "bits/stdc++.h"
#define puba push_back
#define mapa make_pair
#define ff first
#define ss second
#define bend(_x) (_x).begin(), (_x).end()
#define szof(_x) ((int) (_x).size())
#define TASK_NAME aplusb

using namespace std;
typedef long long ll;
typedef pair <int, int> pii;

int n, k;
vector <pii> points;
set <pii> lr, ud;

inline int rm(int pos) {
    lr.erase({points[pos].ff, pos});
    ud.erase({points[pos].ss, pos});
    return 0;
}

inline int add(int pos) {
    lr.insert({points[pos].ff, pos});
    ud.insert({points[pos].ss, pos});
    return 0;
}

int main() {        
    //freopen(TASK_NAME ".in", "r", stdin);
    //freopen(TASK_NAME ".out", "w", stdout);

    scanf("%d%d", &n, &k);

    for (int i = 0; i < n; ++i) {
        int x1, x2, y1, y2;
        scanf("%d%d%d%d", &x1, &y1, &x2, &y2);
        points.puba({x1 + x2, y1 + y2});
        lr.insert({x1 + x2, i});
        ud.insert({y1 + y2, i});
    }

    ll ans = 9e18;
    int bdv = 1 << (2 * k);
    for (int i = 0; i < bdv; ++i) {
        vector <int> removed;
        int tmp = i;
        for (int j = 0; j < k; ++j) {
            int now = tmp % 4;
            if (now == 0) {
                int pos = lr.rbegin() -> ss;
                removed.puba(pos);
                rm(pos);
            } else if (now == 2) {
                int pos = lr.begin() -> ss;
                removed.puba(pos);
                rm(pos);
            } else if (now == 1) {
                int pos = ud.begin() -> ss;
                removed.puba(pos);
                rm(pos);
            } else {
                int pos = ud.rbegin() -> ss;
                removed.puba(pos);
                rm(pos);
            }
            tmp /= 4;
        }
        //cerr << "i: " << i << endl;
        //cerr << lr.begin() -> ff << " " << lr.rbegin() -> ff << endl;
        //cerr << ud.begin() -> ff << " " << ud.rbegin() -> ff << endl;

        ans = min(ans, (ll) (max((lr.rbegin() -> ff - lr.begin() -> ff + 1) / 2, 1)) * (max((ud.rbegin() -> ff - ud.begin() -> ff + 1) / 2, 1)));
        //cerr << ans << " " << i << endl;
        for (int el: removed) {
            add(el);
        }
    }

    cout << ans << "\n";

    return 0;
}

