import java.io.*;
import java.util.*;

public class E {
    BufferedReader br;
    StringTokenizer in;
    PrintWriter out;
    long[] fact;
    long[] invfact;
    final int MOD = 1_000_000_007;
    int[] m;

    public String nextToken() throws IOException {
        while (in == null || !in.hasMoreTokens()) {
            in = new StringTokenizer(br.readLine());
        }
        return in.nextToken();
    }

    public int nextInt() throws IOException {
        return Integer.parseInt(nextToken());
    }

    public double nextDouble() throws IOException {
        return Double.parseDouble(nextToken());
    }

    public long nextLong() throws IOException {
        return Long.parseLong(nextToken());
    }

    public static void main(String[] args) throws IOException {
        new E().run();
    }

    long f(int n, int F) {
        long res = 0;
        for (int i = 1; i * i <= n; i++) {
            if (n % i == 0) {
                res += g(n / i, F) * m[i];
                res %= MOD;
                if (i * i != n) {
                    res += g(i, F) * m[n / i];
                    res %= MOD;
                }
            }
        }
        return res;
    }

    long g(int n, int f) {
        if (n < f) {
            return 0;
        }
        return fact[n - 1] * invfact[f - 1] % MOD * invfact[n - f] % MOD;
    }

    long powMod(long a, int b, int m) {
        long res = 1;
        while (b > 0) {
            if (b % 2 == 1) {
                res = res * a % m;
            }
            a *= a;
            a %= m;
            b /= 2;
        }
        return res;
    }

    public void solve() throws IOException {
        final int MAXN = 100_000;
        m = new int[MAXN + 1];
        for (int i = 1; i < MAXN + 1; i++) {
            int mu = 1;
            int k = i;
            for (int j = 2; j * j <= i; ++j) {
                if (k % j == 0) {
                    mu = -mu;
                    k /= j;
                    if (k % j == 0) {
                        mu = 0;
                        break;
                    }
                }
            }
            if (k > 1) {
                mu = -mu;
            }
            m[i] = mu;
        }
        fact = new long[MAXN + 1];
        invfact = new long[MAXN + 1];
        invfact[0] = 1;
        fact[0] = 1;
        for (int i = 1; i <= MAXN; i++) {
            fact[i] = fact[i - 1] * i % MOD;
            invfact[i] = powMod(fact[i], MOD - 2, MOD);
        }
        int q = nextInt();
        for (int i = 0; i < q; i++) {
            int n = nextInt();
            int F = nextInt();
            out.println(f(n, F));
        }
    }

    public void run() {
        try {
            br = new BufferedReader(new InputStreamReader(System.in));
            out = new PrintWriter(System.out);

            solve();

            out.close();
        } catch (IOException e) {
            e.printStackTrace();
            System.exit(1);
        }
    }
}

