        scanf("%d", a + i);
    for (int j = 1; j <= m; j++)
        scanf("%d", b + j);

    int B = p - 1;
    for (int j = 1; j <= m; j++)
        B = gcd(B, b[j]);

    for (int i = 1; i <= n; i++) {
        int order = getOrder(a[i], p);
        a[i] = gcd((p - 1) / order * B, p - 1);
        // !!
        if (a[i] == 0) a[i] = p - 1;
    }

    int pre = 0, cur = 1;
    dp[cur].clear();
    for (int i = 1; i <= n; i++) {
        swap(pre, cur);
        dp[cur].clear();
        dp[cur][a[i]] = 1;
        FOREACH(e, dp[pre]) {
            //printf("%d %lld\n", e->first, e->second);
            dp[cur][e->first] += e->second;
            dp[cur][lcm(e->first, a[i])] -= e->second;
        }
        //puts("----------");
    }

    LL res = 0;
    FOREACH(e, dp[cur]) {
        //printf("%d %lld\n", e->first, e->second);
        LL add = (p - 1) / e->first;
        res += add * (e->second);
    }

    printf("%d\n", (int) res);

    return 0;
}

