private okhttp3.Response handleExecuteClientCall(final okhttp3.Request request, final int retryCount) throws com.blackducksoftware.integration.exception.IntegrationException {
    if ((client) != null) {
        try {
            logRequestHeaders(request);
            final okhttp3.Response response = client.newCall(request).execute();
            if (!(response.isSuccessful())) {
                if (((response.code()) == 401) && (retryCount < 2)) {
                    connect();
                    return handleExecuteClientCall(request, (retryCount + 1));
                }else {
                    throw new com.blackducksoftware.integration.hub.rest.exception.IntegrationRestException(response.code(), response.message(), java.lang.String.format("There was a problem trying to %s this item: %s. Error: %s %s", request.method(), request.url().uri().toString(), response.code(), response.message()));
                }
            }
            logResponseHeaders(response);
            return response;
        } catch (final java.io.IOException e) {
            throw new com.blackducksoftware.integration.exception.IntegrationException(e.getMessage(), e);
        }
    }else {
        connect();
        return handleExecuteClientCall(request, retryCount);
    }
}

