import java.io.*;
import java.util.*;

public class p608C {
	public static void main(String[] args)
	{
		Scanner in = new Scanner(System.in);
		int n = in.nextInt();
		//int[][] beacons = new int[n][2];
		int max_pos = 0;
		HashMap<Integer,Integer> beacons = new HashMap<Integer,Integer>();
		for(int i = 0; i < n; i++)
		{
			int a = in.nextInt();
			int b = in.nextInt();
			beacons.put(a, b);
			max_pos = Math.max(max_pos, a);
		}

		int[] dp = new int[max_pos+1];
		int max_live = 0;
		for(int i = 1; i < dp.length; i++)
		{
			if(beacons.containsKey(i))
			{
				int tmp = i-beacons.get(i)-1;
				if(tmp < 0) {
					dp[i] = 1;
				}
				else dp[i] = dp[Math.max(0,i-beacons.get(i)-1)] + 1;
			}
			else
			{
				dp[i] = dp[Math.max(0, i-1)];
			}
			max_live = Math.max(max_live, dp[i]);
		}

		System.out.println(n-max_live);
	}
}

