const int N = 305 , inf = 1e9 + 5;
#define ALL(x) x.begin() , x.end()
int arr[N][N];
vector < int > pos[N];
vector < pair < int , int > > type[N];
int dp[N][N];
int n , m , p;
int main(){
	scanf("%d %d %d" , &n , &m , &p);
	for(int i = 1 ; i <= n ; i++){
		for(int j = 1 ; j <= m ; j++){
			scanf("%d" , &arr[i][j]);
			type[arr[i][j]].emplace_back(make_pair(i , j));
		}
	}
	type[0].emplace_back(make_pair(1 , 1));
	pos[1].emplace_back(1);
	for(int col = 1 ; col <= p ; col++){
		for(auto it : type[col]){
			int x = it.first;
			int y = it.second;
			int res = inf;
			for(int i = 1 ; i <= n ; i++){
				auto l = lower_bound(ALL(pos[i]) , y);
				if(l != pos[i].end()){
					res = min(res , abs(i - x) + abs(*l - y) + dp[i][*l]);
				}
				if(l != pos[i].begin()) l--;
				else continue;
				res = min(res , abs(i - x) + abs(*l - y) + dp[i][*l]);
			}
			dp[x][y] = res;
		}
		for(int i = 1 ; i <= n ; i++) pos[i].clear();
		for(auto it : type[col]) pos[it.first].emplace_back(it.second);
	}
	for(int i = 1 ; i <= n ; i++){
		for(int j = 1 ; j <= m ; j++){
			if(arr[i][j] == p){
				printf("%d\n", dp[i][j]);
			}
		}
	}
}

