LL f(LL x){
	LL ans = 0,cnt=0;
	for(int i=63;i>=0;i--){
		if(k==cnt) break;
		if((1ll<<i)&x) ans += c[i][k-cnt], cnt++;
	}
	if(__builtin_popcount(x)==k) ans++;
	return ans;
}

LL bs(LL l, LL r){
	if(l>r) return -1;
	LL mid = (l+r)/2;
	LL cnt = f(mid*2)-f(mid);
	if(cnt==m) return mid;
	if(cnt<m) return bs(mid+1, r);
	else return bs(l, mid-1);
}

