import java.util.HashSet;
import java.util.Scanner;
public class App {
	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt();
		int k = sc.nextInt();
		int[] nodes = new int[n];
		HashSet<Integer> cap = new HashSet<Integer>();
		int c = 0;
		int ans = 0;
		for (int i=0;i<n;++i){
			nodes[i]=sc.nextInt();
			if (i!=0){
				ans += nodes[i-1]*nodes[i];
			}
			if (i==n-1){
				ans += nodes[i]*nodes[0];
			}
		}
		for (int i=0;i<k;++i){
			c =sc.nextInt()-1;
			cap.add(c);
			for (int j=0;j<n;++j){
				if(c==0){
					if(j!=c && j!=c-1 && j!=c+1 && j!=n-1 && !cap.contains(j)){
						ans += nodes[j]*nodes[c];
					}
				}else if(c==n-1){
					if(j!=c && j!=c-1 && j!=c+1 && j!=0 && !cap.contains(j)){
						ans += nodes[j]*nodes[c];
					}
				}else{
					if(j!=c && j!=c-1 && j!=c+1 && !cap.contains(j)){
						ans += nodes[j]*nodes[c];
					}
				}
			}
		}
		System.out.println(ans);
	}
}

