inline int read(){
    int x=0;
    bool w=0;
    char c=getchar();
    while(!isdigit(c))  w|=c=='-',c=getchar();
    while(isdigit(c))   x=(x<<1)+(x<<3)+(c^48),c=getchar();
    return w?-x:x;
}
inline void print(int x){
    if(x<0) x=-x,putchar('-');
    if(x>9) print(x/10);
    putchar('0'+x%10);
}
const int N=2e5+5;
int tree[N*3];
int head[N],top[N],siz[N],son[N],a[N],id[N],fa[N],dep[N],f[N][22];
int n,tot,num,flag;
struct node{
    int to,nxt;
}Edge[N<<1];
inline void add(int u,int v){
    Edge[++tot].to=v;
    Edge[tot].nxt=head[u];
    head[u]=tot;
}
#define ls(x) x<<1
#define rs(x) x<<1|1
inline void push_up(int p){
    tree[p]=tree[ls(p)]+tree[rs(p)];
}
inline void update(int p,int l,int r,int pl,int pr,int k){
    if(l>=pl && r<=pr){
        tree[p]=k;
        return ;
    }
    int mid=l+r>>1;
    if(pl<=mid) update(ls(p),l,mid,pl,pr,k);
    if(pr>mid)  update(rs(p),mid+1,r,pl,pr,k);
    push_up(p);
}
inline int query(int p,int l,int r,int pl,int pr){
    if(l>=pl &&r<=pr)   return tree[p];
    int mid=l+r>>1,res=0;
    if(pl<=mid) res+=query(ls(p),l,mid,pl,pr);
    if(pr>mid)  res+=query(rs(p),mid+1,r,pl,pr);
    return res;
}
inline int Query(int x,int y){
    int res=0;
    while(top[x]!=top[y]){
        if(dep[top[x]]<dep[top[y]])   swap(x,y);
        res+=query(1,1,n,id[x],id[top[x]]);
        x=fa[top[x]];
    }
    if(dep[x]>dep[y])   swap(x,y);
    res+=query(1,1,n,id[x],id[y]);
    return res;
}
inline void dfs(int x,int Fa){
	f[x][0]=Fa;
	for(register int i=1;i<=20;++i){
		f[x][i]=f[f[x][i-1]][i-1];
	}
	for(register int i=head[x];i;i=Edge[i].nxt){
		int v=Edge[i].to;
		if(v!=Fa)	dfs(v,x);
	}
}
inline int lca(int x,int y){
	if(dep[x]<dep[y])	swap(x,y);
	for(register int i=20;~i;--i){
		if(dep[f[x][i]]>=dep[y])	x=f[x][i];
	}
	if(x==y)	return x;
	for(register int i=20;~i;--i)
		if(f[x][i]!=f[y][i])	x=f[x][i],y=f[y][i];
	return f[x][0];
}
inline bool Solve(){
    int cnt=0,k=read();
    for(register int i=1;i<=k;++i)  a[i]=read();
    if(k==1)    return 1;
    for(register int i=1;i<=k;++i){
        update(1,1,n,id[a[i]],id[a[i]],1);
    }
    //for(register int i=1;i<n*2;++i)
    //    cout<<tree[i]<<' ';cout<<endl;
    vector<int> point;
    for(register int i=1;i<=k;++i){
        if(query(1,1,n,id[a[i]],id[a[i]]+siz[a[i]]-1)==1)  ++cnt,point.push_back(a[i]);
    //    cout<<i<<' '<<id[a[i]]<<' '<<siz[a[i]]<<' '<<a[i]<<" iiii\n";
    }
    int check_extra=-1;
    if(point.size()==2) check_extra=Query(1,fa[lca(point[0],point[1])]);
    for(register int i=1;i<=k;++i){
        update(1,1,n,id[a[i]],id[a[i]],0);
    }
    //cout<<cnt<<" cnt\n";
    if(cnt==1)  return 1;
    if(cnt==2)  return check_extra==0;
    return 0;
}
inline void dfs1(int x,int f){
    fa[x]=f,siz[x]=1,dep[x]=dep[f]+1;
    for(register int i=head[x];i;i=Edge[i].nxt){
        int v=Edge[i].to;
        if(v==f)    continue;
        dfs1(v,x);
        siz[x]+=siz[v];
        if(siz[v]>siz[son[x]])  son[x]=v;
    }
}
inline void dfs2(int x,int topx){
    id[x]=++num;
    top[x]=topx;
    if(!son[x]) return ;
    dfs2(son[x],topx);
    for(register int i=head[x];i;i=Edge[i].nxt){
        int v=Edge[i].to;
        if(v==fa[x] || v==son[x])   continue;
        dfs2(v,v);
    }
}

