	}
	return res;
}
int n;
vector<int> g[200001];
int dp[200001];
int arr[200001];
int dfs(int par, int gpar)
{
	dp[par]=arr[par];
	for(int i=0;i<g[par].size();i++)
	{
		int child=g[par][i];
		if(child!=gpar)
		dp[par]+=dfs(child, par);
	}
	return dp[par];
}
int maxans=-1e17;
bool isposs=false;
int findone(int par, int gpar)
{
	int maxchild=-1;
	int maxchildval=-1;
	int secmaxchildval=-1;
	for(int i=0;i<g[par].size();i++)
	{
		int child=g[par][i];
		if(child!=gpar)
		{
			int val=findone(child, par);
			if(maxchild==-1||val>=dp[maxchild])
			{
				maxchild=child;
				maxchildval=val;
			}
		}
	}
	if(maxchild==-1)
	{
		return dp[par];
	}
	int secmaxchild=-1;
	for(int i=0;i<g[par].size();i++)
	{
		int child=g[par][i];
		if(child!=gpar&&child!=maxchild)
		{
			int val=findone(child, par);
			if(secmaxchild==-1||val>=dp[secmaxchild])
			{
				secmaxchild=child;
				secmaxchildval=val;
			}
		}
	}
	//cout<<par<<"-->";
	//cout<<maxchild<<" "<<secmaxchild<<"\n";
	if(secmaxchild==-1)
	{
		//cout<<"returning "<<max(dp[maxchild], dp[par])<<"\n";
		return max(maxchildval, dp[par]);
	}
	else
	{
		maxans=max(maxans, maxchildval+secmaxchildval);
		isposs=true;
		//cout<<maxans<<"\n";
		//cout<<"returning "<<max(dp[maxchild], dp[par])<<"\n";
		return max(maxchildval, dp[par]);
	}
}
