#include <bits/stdc++.h>

#define F first
#define S second

using namespace std;

typedef long long int LL;

int a[100005];

struct Node {
    LL n[10];
    //vector<unsigned char> lazy;
    int lazy[10];
};

Node tree[100005 << 2];

void build(int node, int start, int end) {
    if (start == end) {
        LL now = a[start], qq = 1;
        while (now) {
            tree[node].n[now % 10] += qq;
            now /= 10;
            qq *= 10;
        }
        for (int i = 0; i < 10; ++i) tree[node].lazy[i] = i;
    } else {
        int mid = (start + end) / 2;
        build(2*node, start, mid);
        build(2*node + 1, mid + 1, end);
        for (int i = 0; i < 10; ++i) {
            tree[node].n[i] = tree[2*node].n[i] + tree[2*node + 1].n[i];
        }
        for (int i = 0; i < 10; ++i) tree[node].lazy[i] = i;
    }
}
int lazy1[10], lazy2[10], nn[10];
void push(int node, int start, int end) {
    memset(nn, 0, sizeof(nn));
    if (start != end) {
        for (int i = 0; i < 10; ++i) {
            lazy1[i] = tree[node].lazy[tree[node*2].lazy[i]];
            lazy2[i] = tree[node].lazy[tree[node*2+1].lazy[i]];
        }
        for (int i = 0; i < 10; ++i) {
            tree[node*2].lazy[i] = lazy1[i];
            tree[node*2+1].lazy[i] = lazy2[i];
        }
    }
    for (int i = 0; i < 10; ++i) {
        nn[tree[node].lazy[i]] += tree[node].n[i];
    }
    for (int i = 0; i < 10; ++i) {
        tree[node].n[i] = nn[i];
        tree[node].lazy[i] = i;
    }
}

void updateRange(int node, int start, int end, int l, int r, int x, int y) {
    if (x == y) return;
    push(node, start, end);

    if (start > end or start > r or end < l) return;

    if (start >= l and end <= r) {
        tree[node].lazy[x] = y;
        push(node, start, end);
        return;
    }
    int mid = (start + end) / 2;
    updateRange(node*2, start, mid, l, r, x, y);
    updateRange(node*2 + 1, mid + 1, end, l, r, x, y);
    for (int i = 0; i < 10; ++i) {
        tree[node].n[i] = tree[2*node].n[i] + tree[2*node + 1].n[i];
    }
}

LL queryRange(int node, int start, int end, int l, int r) {
    if (start > end || start > r || end < l) return 0;
    push(node, start, end);
    if (start >= l and end <= r) {
        LL res = 0;
        for (int i = 0; i < 10; ++i) {
            res += tree[node].n[i] * i;
        }
        return res;
    }
    int mid = (start + end) / 2;
    LL p1 = queryRange(node*2, start, mid, l, r);
    LL p2 = queryRange(node*2 + 1, mid + 1, end, l, r);
    return (p1 + p2);
}

int main()
{
    cin.tie(0);
    cin.sync_with_stdio(0);
    LL n, q;
    cin >> n >> q;
    for (int i = 1; i <= n; ++i) cin >> a[i];
    build(1, 1, n);
    int op, l, r, x, y;
    while (q--) {
        cin >> op >> l >> r;
        if (op == 1) {
            cin >> x >> y;
            updateRange(1, 1, n, l, r, x, y);
        } else {
            cout << queryRange(1, 1, n, l, r) << '\n';
        }
    }
}

