import java.util.Random;

public class P612D {

    public static class Record{
        int point;
        int status;
        public Record(int point, int status){
            this.point = point;
            this.status = status;
        }

        public int compare(Record r){
            if (this.point < r.point){
                return -1;
            } else if (this.point > r.point){
                return 1;
            } else if (this.status == -1){
                return -1;
            } else if (r.status == -1){
                return 1;
            } else {
                return 0;
            }
        }

        public String toString(){
            return this.point+" "+this.status;
        }
    }

    public static class Interval{
        int start;
        int end;

        public Interval(int start, int end){
            this.start = start;
            this.end = end;
        }

        public String toString(){
            return this.start + " " + this.end;
        }
    }

    public static class FastScanner{
        private BufferedReader bi;
        String line;
        public FastScanner(){
            this.bi = new BufferedReader(new InputStreamReader(System.in));
        }

        public int[] readIntPair() {
            try {
                this.line = this.bi.readLine();
            } catch (IOException e) {
                e.printStackTrace();
            }
            String[] spl = this.line.split("\\s");
            return new int[]{Integer.parseInt(spl[0]), Integer.parseInt(spl[1])};
        }
    }

    static Record[] a;
    public static void main(String[] args) {
        FastScanner sc = new FastScanner();
        PrintWriter out = new PrintWriter(System.out);
        int[] pp = sc.readIntPair();

        int n = pp[0];
        int k = pp[1];

        Record[] lefts = new Record[n];
        Record[] rights = new Record[n];
        int n2 = 2*n;
        for (int i = 0; i < n; i++){
            pp = sc.readIntPair();
			lefts[i] = new Record(pp[0], -1);
            rights[i] = new Record(pp[1], 1);
        }
//        Comparator<Record> com = new Comparator<Record>() {
//            @Override
//            public int compare(Record o1, Record o2) {
//                if (o1.point < o2.point){
//                    return -1;
//                } else if (o1.point > o2.point){
//                    return 1;
//                } else if (o1.status == -1){
//                    return -1;
//                } else if (o2.status == -1){
//                    return 1;
//                } else {
//                    return 0;
//                }
//            }
//        };
        sortArray(lefts);
        sortArray(rights);
//        Arrays.sort(lefts, com);
//        Arrays.sort(rights, com);
        int count = 0;
        int left = 0;
        ArrayList<Interval> ans = new ArrayList<Interval>(n);

        int x = 0;
        int y = 0;
        Record current;
        for (int i = 0, l = 2*n; i < l; i++){
            if (x >= n){
                current = rights[y++];
            } else if (y >= n){
                current = lefts[x++];
            } else {
                Record o1 = lefts[x];
                Record o2 = rights[y];
                if (o1.compare(o2) == -1){
                    current = o1;
                    x++;
                } else {
                    current = o2;
                    y++;
                }
            }
            count -= current.status;
            if (count == k && count+current.status == k-1){
                left = current.point;
            } else if (count == k-1 && count+current.status == k){
                ans.add(new Interval(left, current.point));
            }
        }

        out.write(ans.size()+"\n");
		for (Interval in : ans){
			out.write(in.toString()+"\n");
		}
        out.flush();;
        out.close();
    }

    public static Record[] sortArray(Record[] array){
        a = array;
        sort(0, array.length - 1, new Random());
        return a;
    }

    public static void sort(int left, int right, Random r) {
		if (right - left <= 0) {
			return;
		}
		int pivotIndex;
		pivotIndex = left + r.nextInt(right - left);
		swap(left, pivotIndex);
		pivotIndex = left;
		int i = pivotIndex + 1;
		for (int j = pivotIndex + 1; j <=right; j++) {
			if (a[j].compare(a[pivotIndex]) == -1) {
				swap(i, j);
				i++;
			}
		}
		swap(pivotIndex, i - 1);
		sort(left, i - 2, r);
		sort(i, right, r);
	}

	public static void swap(int pos1, int pos2) {
		Record tmp = a[pos1];
		a[pos1] = a[pos2];
		a[pos2] = tmp;
	}
}

