#include <bits/stdc++.h>
using namespace std;

int n;
string s, ta, tb;
string t, ap;
char m1[256], m2[256];
char term;

bool dfs(int i, int status) {
  if(i == s.size()) return true;

  if(m1[s[i]]) {
    if(status == 0) {
      if(m1[s[i]] < ta[i]) return false;
      if(m1[s[i]] == ta[i]) return dfs(i + 1, 0);
      if(m1[s[i]] > ta[i]) return dfs(i + 1, 1);
    } else {
      return dfs(i + 1, 1);
    }
  } else if(status == 0) {
    if(!m2[ta[i]]) {
      m1[s[i]] = ta[i];
      m2[ta[i]] = s[i];
      if(dfs(i + 1, 0)) return true;
      m1[s[i]] = 0;
      m2[ta[i]] = 0;
    }
    for(char j = ta[i] + 1; j <= term; j++) {
      if(m2[j]) continue;
      m1[s[i]] = j;
      m2[j] = s[i];
      return dfs(i + 1, 1);
    }
    char c = m1[s[i]];
    m1[s[i]] = 0;
    m2[c] = 0;
    return false;
  } else {
    for(char j = 'a'; j <= term; j++) {
      if(m2[j]) continue;
      m1[s[i]] = j;
      m2[j] = s[i];
      return dfs(i + 1, 1);
    }
    char c = m1[s[i]];
    m1[s[i]] = 0;
    m2[c] = 0;
    return false;
  }
}

bool solve() {
  if(!dfs(0, 0)) return false;

  for(int i = 'a', j = 'a'; i <= term && j <= term; ) {
    if(m1[i]) {
      i++;
      continue;
    }
    if(m2[j]) {
      j++;
      continue;
    }
    m1[i] = j;
    m2[j] = i;
    i++; j++;
  }

  for(auto c: s) {
    ap.push_back(m1[c]);
  }
  if(ap > tb) return false;

  for(char c = 'a'; c <= term; c++) {
    t.push_back(m1[c]);
  }

  return true;
}

int main() {
  int T; cin >> T;
  while(T--) {
    memset(m1, 0, sizeof m1);
    memset(m2, 0, sizeof m2);
    cin >> n;
    cin >> s >> ta >> tb;
    t.clear(); ap.clear();
    term = 'a' + n - 1;
    int ret = solve();
    if(ret) {
      printf("YES\n");
      cout << t << endl;
    } else {
      printf("NO\n");
    }
  }
}

#include <bits/stdc++.h>
using namespace std;
const int N = 200005;
using ll = long long;
int n,m,deg[N],rt[N],ridx,in[N],out[N],dptr;char s[N];
inline int id(int x,int y) {return (x - 1) * m + y;}
vector<int> v[N];
void dfs(int pos)
{
	in[pos] = ++dptr;for (auto &i : v[pos]) dfs(i);
	out[pos] = dptr;
}
struct Rect {int u,d,l,r;} r[N];
int idxq,idxr;struct Query {int u,d,p,v;} q[N];
struct TNode {int l,r,v;} t[N << 2];int lazy[N << 2];
inline int calc(int pos,int l,int r) {return !lazy[pos] ? t[pos].v : r - l + 1;}
inline int lc(int pos) { return pos << 1; }
inline int rc(int pos) { return pos << 1 | 1; }
void build(int pos,int l,int r)
{
	t[pos].l = l;t[pos].r = r;if (l == r) return;
	int mid = (l + r) >> 1;build(lc(pos),l,mid);build(rc(pos),mid + 1,r);
}
void modify(int pos,int l,int r,int v)
{
	if (l == t[pos].l && r == t[pos].r) {lazy[pos] += v;return;}
	int mid = (t[pos].l + t[pos].r) >> 1;
	if (r <= mid) modify(lc(pos),l,r,v);
	else if (l > mid) modify(rc(pos),l,r,v);
	else modify(lc(pos),l,mid,v),modify(rc(pos),mid + 1,r,v);
	t[pos].v = calc(lc(pos),t[pos].l,mid) + calc(rc(pos),mid + 1,t[pos].r);
}
int main ()
{
	ios::sync_with_stdio(false);
	cin >> n >> m;
	for (int i = 1;i <= n;i++) cin >> (s + (i - 1) * m + 1);
	for (int i = 1;i <= n;i++)
		for (int j = 1;j <= m;j++)
		{
			if (i - 2 >= 1 && s[id(i - 2,j)] == 'U')
				v[id(i,j)].push_back(id(i - 2,j)),++deg[id(i - 2,j)];
			if (j - 2 >= 1 && s[id(i,j - 2)] == 'L')
				v[id(i,j)].push_back(id(i,j - 2)),++deg[id(i,j - 2)];
			if (i + 2 <= n && s[id(i + 2,j)] == 'D')
				v[id(i,j)].push_back(id(i + 2,j)),++deg[id(i + 2,j)];
			if (j + 2 <= m && s[id(i,j + 2)] == 'R')
				v[id(i,j)].push_back(id(i,j + 2)),++deg[id(i,j + 2)];
		}
	build(1,1,n * m + 1);for (int i = 1;i <= n * m;i++) if (!deg[i]) rt[++ridx] = i;
	for (int i = 1;i <= ridx;i++) dfs(rt[i]);
	for (int i = 1;i <= n;i++)
		for (int j = 1;j <= m;j++)
			if (s[id(i,j)] == 'U' && s[id(i + 1,j)] == 'D')
			{
				r[++idxr] = {in[id(i,j)],out[id(i,j)],in[id(i + 1,j)],out[id(i + 1,j)]};
				r[++idxr] = {in[id(i + 1,j)],out[id(i + 1,j)],in[id(i,j)],out[id(i,j)]};
			}
			else if (s[id(i,j)] == 'L' && s[id(i,j + 1)] == 'R')
			{
				r[++idxr] = {in[id(i,j)],out[id(i,j)],in[id(i,j + 1)],out[id(i,j + 1)]};
				r[++idxr] = {in[id(i,j + 1)],out[id(i,j + 1)],in[id(i,j)],out[id(i,j)]};
			} 
	for (int i = 1;i <= idxr;i++)
	{
		q[++idxq] = {r[i].u,r[i].d,r[i].l,1};
		q[++idxq] = {r[i].u,r[i].d,r[i].r + 1,-1};
	}
	sort(q + 1,q + idxq + 1,[](Query a,Query b) {return a.p < b.p;});
	ll ans = 0;
	for (int i = 1;i <= idxq;i++)
	{
		modify(1,q[i].u,q[i].d,q[i].v);
		if (i != idxq) ans += calc(1,1,n) * ll(q[i + 1].p - q[i].p);
	}
	cout << ans / 2 << endl;
	return 0;
}

