#include<bits/stdc++.h>

#define clr(x,y) memset((x),(y),sizeof(x))

using namespace std;
typedef long long LL;

const int maxn=300;

int A[maxn+5][maxn+5];
int dp[maxn+5][maxn+5][maxn+5];
int dx[]={-1,-1,0,0};
int dx2[]={-1,0,-1,0};

int n;

int main(void)
{
	#ifdef ex
	freopen ("../in.txt","r",stdin);
	//freopen ("../out.txt","w",stdout);
	#endif

	scanf("%d",&n);
	for (int i=1;i<=n;++i)
    {
        for (int j=1;j<=n;++j)
        {
            scanf("%d",&A[i][j]);
        }
    }

    int x1,y1,x2,y2,tmp;

    clr(dp,-127);

    //cout<<dp[1][1][1]<<' '<<dp[2][1][1]<<endl;
    dp[2][1][1]=A[1][1];
    for (int i=3;i<=2*n;++i)
    {
        for (int j=1;j<=min(i-1,n);++j)
        {
            for (int k=1;k<=min(i-1,n);++k)
            {
                for (int q=0;q<=3;++q)
                {
                    x1=j+dx[q];
                    x2=k+dx2[q];
                    y1=i-1-x1;
                    y2=i-1-x2;

                    if (x1<=0 || x1>n || y1<=0 || y1>n) continue;
                    if (x2<=0 || x2>n || y2<=0 || y2>n) continue;

                    if (j==k) tmp=A[j][i-j];
                    else tmp=A[j][i-j]+A[k][i-k];

                    dp[i][j][k]=max(dp[i-1][x1][x2]+tmp,dp[i][j][k]);
                }
                //printf("%d %d %d %d\n",i,j,k,dp[i][j][k]);
            }
        }
    }

    int ans=dp[2*n][n][n];
    printf("%d\n",ans);
}

