#define N 1000010
typedef long long ll;
#define int ll
int n,m,st,cnt,h[N],scc[N],id,q[N],top,dfn[N],low[N],in[N],num;ll w[N],ans,s1[N],s2[N],dp[N],vis[N];
struct edge{int v;ll w;int nxt;}e[N*2];
struct line{int v;ll w;};
vector<line> g[N];
void add(int u,int v,ll w){e[++cnt]=(edge){v,w,h[u]};h[u]=cnt;}
void tarjan(int u)
{
	dfn[u]=low[u]=++num;
	q[++top]=u;in[u]=1;
	for(int i=h[u];i;i=e[i].nxt)
	{
		int v=e[i].v;
		if(dfn[v]&&in[v]) low[u]=min(low[u],dfn[v]);
		else if(!dfn[v]) tarjan(v),low[u]=min(low[u],low[v]);
	}
	if(dfn[u]==low[u])
	{
		int t=q[top];id++;
		while(t!=u) scc[t]=id,in[t]=0,t=q[--top];
		scc[t]=id;top--;in[t]=0;
	}
}
ll dfs(int u)
{
	if(vis[u]) return dp[u];
	vis[u]=1;
	for(int i=0;i<g[u].size();i++)
	  dp[u]=max(dp[u],dfs(g[u][i].v)+g[u][i].w);
	dp[u]+=w[u];ans=max(ans,dp[u]);return dp[u];
}
ll sum(int x)
{
	return (ll)(sqrt(1+x*8)-1)/2;
}

ll cont(ll a)
{
	int k=1.0*sqrt(0.25+2*a)-0.5;
	return a*k-k*(k+1)*(k+2)/6+a;
}
