	cin>>st;tarjan(st);//caicout<<id<<"\n";
	for(int i=1;s2[i-1]+i*i<1e9;i++) s2[i]=s2[i-1]+i*i;
	for(int i=1;s1[i-1]+i<N;i++) s1[i]=s1[i-1]+i;
	for(int u=1;u<=n;u++)
	  for(int i=h[u];i;i=e[i].nxt)
	  {
		  int v=e[i].v;ll val=e[i].w,tmp;
		  if(scc[u]==scc[v]) w[scc[u]]+=cont(val);

		  else g[scc[u]].push_back((line){scc[v],e[i].w});//cout<<sum(val)<<"\n";
	  }
	dfs(scc[st]);cout<<ans;
	return 0;
}

